import React, { Component } from "react";

class App extends Component {
  constructor() {
    super();
    this.state = {
      newItem: "",
      list: [],
    };
  }

  updateInput(key, value) {
    this.setState({
      [key]: value,
    });
  }

  addItem(event) {
    event.preventDefault();
    const list = [...this.state.list];
    const newItem = {
      id: 1 + Math.random(),
      value: this.state.newItem,
    };

    if (newItem.value === "") {
      alert("Please add a task to do");
    } else {
      list.push(newItem);
    }

    this.setState({
      list,
      newItem: "",
    });
  }

  render() {
    return (
      <div id="App">
        <p id="header">Todos</p>
        <input
          placeholder="Add task"
          value={this.state.newItem}
          onChange={(e) => this.updateInput("newItem", e.target.value)}
        />
        <button id="add-btn" onClick={() => this.addItem()}>
          Add
        </button>
        <ul>
          {this.state.list.map((item, i) => {
            return <li key={i}>{item.value}</li>;
          })}
        </ul>
      </div>
    );
  }
}
export default App;
