import React, { useState } from "react";

function noClassApp() {
  const [task, setTask] = useState();
  const listArr = [];

  function add() {
    const list = [...list];
    const listItem = {
      id: 1 + Math.random(),
      value: task,
    };

    listItem.value === ""
      ? alert("Please add a task to do")
      : list.push(listItem);

    setTask({
      list,
      task: "",
    });
  }
  return (
    <section id="main">
      <h3 id="header">Todos</h3>
      <div>
        <input
          placeholder="Add task"
          value={task}
          onChange={(e) => setTask(e.target.value)}
        />
        <button id="btn" onClick={function add() {}}>
          Add
        </button>
      </div>
      <ul>
        {listArr.map((i) => {
          return <li key={i}>{i.value}</li>;
        })}
      </ul>
    </section>
  );
}

export default noClassApp;
