import React, { useState } from "react";

function Aaa() {
  const [task, setTask, list] = useState();

  listItem.value === ""
    ? alert("Please add a task to do")
    : list.push(listItem);

  return (
    <section id="main">
      <h3 id="header">Todos</h3>
      <div>
        <input
          placeholder="Add task"
          value={task}
          onChange={(e) => setTask("task", e.target.value)}
        />
        <button id="btn" onClick={() => add()}>
          Add
        </button>
      </div>
      <ul>
        {list.map((i) => {
          return <li key={i}>{i.value}</li>;
        })}
      </ul>
    </section>
  );
}

export default Aaa;
