import React from "react";
import Run from "./Render";
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      newItem: "",
      list: [],
    };
  }

  updateInput(key, value) {
    this.setState({
      [key]: value,
    });
  }

  addItem() {
    const list = [...this.state.list];
    const newItem = {
      id: 1 + Math.random(),
      value: this.state.newItem,
    };

    if (newItem.value === "") {
      alert("Please add a task to do");
    } else {
      list.push(newItem);
    }

    this.setState({
      list,
      newItem: "",
    });
  }

  render() {
    <React.StrictMode>
      <Run />
    </React.StrictMode>;
  }
}
export default App;
