import React from "react";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      task: "",
      list: [],
    };
  }

  updateInput(key, value) {
    this.setState({
      [key]: value,
    });
  }

  add() {
    const list = [...this.state.list];
    const listItem = {
      id: 1 + Math.random(),
      value: this.state.task,
    };

    if (listItem.value === "") {
      alert("Please add a task to do");
    } else {
      list.push(listItem);
    }

    this.setState({
      task: "",
    });
  }

  render() {
    return (
      <section id="main">
        <h3 id="header">Todos</h3>
        <div>
          <input
            placeholder="Add task"
            value={this.state.task}
            onChange={(e) => this.updateInput("task", e.target.value)}
          />
          <button id="btn" onClick={() => this.add()}>
            Add
          </button>
        </div>
        <ul>
          {this.state.list.map((item, i) => {
            return <li key={i}>{item.value}</li>;
          })}
        </ul>
      </section>
    );
  }
}
export default App;
