import React, { useState } from "react";

function Aaa() {
  const [task, setTask] = useState();
  function add() {
    const listItem = {
      id: 1 + Math.random(),
      value: task,
    };

    listItem.value === ""
      ? alert("Please add a task to do")
      : task.push(listItem);
  }

  function handleInput(e) {
    setTask(e.target.value);
  }

  return (
    <section id="main">
      <h3 id="header">Todos</h3>
      <div>
        <input
          placeholder="Add task"
          value={task}
          onChange={(e) => handleInput("task", e.target.value)}
        />
        <button id="btn" onClick={() => add()}>
          Add
        </button>
      </div>
      <ul>
        {task.map((i) => {
          return <li key={i}>{i.value}</li>;
        })}
      </ul>
    </section>
  );
}

export default Aaa;
