import React from "react";

function Aaa() {
  function add() {
    const list = [...this.state.list];
    const listItem = {
      id: 1 + Math.random(),
      value: this.state.task,
    };

    listItem.value === ""
      ? alert("Please add a task to do")
      : list.push(listItem);

    this.setState({
      list,
      task: "",
    });
  }

  function handleInput(key, value) {
    this.setState({
      [key]: value,
    });
  }

  function srender() {
    return (
      <section id="main">
        <h3 id="header">Todos</h3>
        <div>
          <input
            placeholder="Add task"
            value={this.state.task}
            onChange={(e) => handleInput("task", e.target.value)}
          />
          <button id="btn" onClick={() => add()}>
            Add
          </button>
        </div>
        <ul>
          {list.map((i) => {
            return <li key={i}>{i.value}</li>;
          })}
        </ul>
      </section>
    );
  }
}
export default Aaa;
