import React from "react";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      task: "",
      list: [],
    };
  }

  add() {
    const list = [...this.state.list];
    const listItem = {
      id: 1 + Math.random(),
      value: this.state.task,
    };

    if (listItem.value === "") {
      alert("Please add a task to do");
    } else {
      list.push(listItem);
    }

    this.setState({
      list,
      task: "",
    });
  }

  render() {
    return (
      <section id="main">
        <p id="header">Todos</p>
        <input placeholder="Add task" value={this.state.task} />
        <button id="btn" onClick={() => this.add()}>
          Add
        </button>
        <ul>
          {this.state.list.map((item, i) => {
            return <li key={i}>{item.value}</li>;
          })}
        </ul>
      </section>
    );
  }
}
export default App;
