import React, { useState } from "react";

function App(props) {
  const [task] = useState(task);

  function add() {
    const list = [...this.state.list];
    const listItem = {
      id: 1 + Math.random(),
      value: this.state.task,
    };

    listItem.value === ""
      ? alert("Please add a task to do")
      : list.push(listItem);

    this.setState({
      list,
      task: "",
    });
  }

  function setTask(e) {
    e.target.value;
  }

  return (
    <section id="main">
      <h3 id="header">Todos</h3>
      <div>
        <input
          placeholder="Add task"
          value={task}
          onChange={(e) => this.setTask()}
        />
        <button id="btn" onClick={() => this.add()}>
          Add
        </button>
      </div>
      <ul>
        {this.state.list.map((i) => {
          return <li key={i}>{i.value}</li>;
        })}
      </ul>
    </section>
  );
}

export default App;
